    private void btnaddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnaddActionPerformed
        Employee temp;
        String nm, type;
        int hours;
        double rate;
        if (num==10){
            btnadd.setEnabled(false);
            JOptionPane.showMessageDialog(this, "10 employees are already entered");
        }
        try{
            nm=txtname.getText();
            hours = Integer.parseInt(txthours.getText());
            rate = Double.parseDouble(txtrate.getText());
            type= bgstatus.getSelection().getActionCommand();
        }catch(Exception e)
        {
            JOptionPane.showMessageDialog(this, "Must fill out form correctly");
            return;
        }
        if(type.equals("FT")){
            temp= new FullTimeEmployee();
        }
        else{
            temp=new PartTimeEmployee();
        }
        if (temp.setName(nm)&& temp.setHours(hours)&& temp.setRate(rate)){
            emp[size] = temp;
            tblpay.setValueAt(temp.getName(), size, 0);
            tblpay.setValueAt(nf.format(temp.getPay()),size,1);
            size++;
            lbltotalpay.setText(nf.format(Employee.getTotalPay()));
            num++;
            clearform();
            return;
            
        }
        
        String error="ERROR\n=====\n";
        if (temp.setName(nm)==false) error+= "Name: " + Employee.getNameRules() +"\n";
        if (temp.setHours(hours)==false) error+= "Hours: " + Employee.getHourRules() +"\n";
        if (temp.setRate(rate)==false) error+= "Rate: " + Employee.getRateRules() +"\n";
        JOptionPane.showMessageDialog(this, error);
        
    }//GEN-LAST:event_btnaddActionPerformed

