    private void btnrulesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnrulesActionPerformed
        String rules="The rules are: \n";
        rules+=Employee.getNameRules()+"\n";
        rules+=Employee.getRateRules()+"\n";
        rules+=Employee.getHourRules()+"\n";
        JOptionPane.showMessageDialog(this, rules);
        
    }//GEN-LAST:event_btnrulesActionPerformed

    public void clearform(){
        txtname.setText("");
        txtrate.setText("");
        txthours.setText("");
        bgstatus.clearSelection();
    }
